	tens_digit_svg = MENU_NUMBER_SVG_FUNCTIONS[get_digit(MINE_COUNT, TENS_DIGIT_DECIMAL_PLACE_INDEX)](FLAGS_REMAINING_TENS_DIGIT_X_POSITION_OFFSET, FLAGS_REMAINING_Y_POSITION_OFFSET);
	ones_digit_svg = MENU_NUMBER_SVG_FUNCTIONS[get_digit(MINE_COUNT, ONES_DIGIT_DECIMAL_PLACE_INDEX)](FLAGS_REMAINING_ONES_DIGIT_X_POSITION_OFFSET, FLAGS_REMAINING_Y_POSITION_OFFSET);

	return hundreds_digit_svg + tens_digit_svg + ones_digit_svg;
